INSERT INTO `ecs_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('179', '12', 'ECS000179', '航班：成都-悉尼', '+', '8', '0', '', '1000', '0.000', '6720.00', '5600.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;</p>\r\n<ul class=\"flight_tips_list clearfix\" style=\"padding: 0px; margin: 7px 0px 0px; list-style: none; zoom: 1; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; line-height: 21px;\">\r\n    <li style=\"padding: 0px; margin: 0px 7px 0px 6px; float: left; width: 490px; height: 442px;\">\r\n    <div class=\"img\" style=\"padding: 0px; margin: 0px 0px 10px; overflow: hidden; height: 302px;\"><img alt=\"\" src=\"http://images4.c-ctrip.com/target/600o080000003izrg1DFC.jpg\" style=\"border: 0px; width: 490px;\" /></div>\r\n    <p class=\"img_txt\" style=\"padding: 0px; margin: 0px; line-height: 1.5; font-size: 15px;\">&nbsp;</p>\r\n    <h4 style=\"padding: 0px; margin: 0px;\">2016年11月11日，国航直飞悉尼荣耀启航！届时，不仅成都旅客可一步直达南半球的阳光海港，西安、昆明、乌鲁木齐、贵阳、兰州、西宁等中国西部多地的旅客也可通过成都轻松飞越赤道，看看世界的另一面。</h4>\r\n    <p style=\"padding: 0px; margin: 0px; line-height: 1.5;\">&nbsp;</p>\r\n    </li>\r\n    <li style=\"padding: 0px; margin: 0px 7px 0px 6px; float: left; width: 490px; height: 442px;\">\r\n    <div class=\"img\" style=\"padding: 0px; margin: 0px 0px 10px; overflow: hidden; height: 302px;\"><img alt=\"\" src=\"http://images4.c-ctrip.com/target/600s080000003j0hzB9D7.jpg\" style=\"border: 0px; width: 490px;\" /></div>\r\n    <p class=\"img_txt\" style=\"padding: 0px; margin: 0px; line-height: 1.5; font-size: 15px;\">&nbsp;</p>\r\n    <h4 style=\"padding: 0px; margin: 0px;\">澳大利亚的发源地悉尼是如今大洋洲最大的城市，空间、阳光、自由是悉尼给人最深刻的印象。这里有绝佳的水景可供欣赏，只需短短的车程就可以从城市的喧嚣中逃脱，围绕悉尼的海滩及森林使每个人保持清醒。</h4>\r\n    <p style=\"padding: 0px; margin: 0px; line-height: 1.5;\">&nbsp;</p>\r\n    </li>\r\n    <li style=\"padding: 0px; margin: 0px 7px 0px 6px; float: left; width: 490px; height: 442px;\">\r\n    <div class=\"img\" style=\"padding: 0px; margin: 0px 0px 10px; overflow: hidden; height: 302px;\"><img alt=\"\" src=\"http://images4.c-ctrip.com/target/600k080000003j23rADDF.jpg\" style=\"border: 0px; width: 490px;\" /></div>\r\n    <p class=\"img_txt\" style=\"padding: 0px; margin: 0px; line-height: 1.5; font-size: 15px;\">&nbsp;</p>\r\n    <h4 style=\"padding: 0px; margin: 0px;\">在澳大利亚悉尼的杰克逊海港，有一座号称世界第一单孔拱桥的宏伟大桥，这就是著名的悉尼海港大桥(Sydney Harbour Bridge, Australia)。悉尼海港大桥是早期悉尼的代表建筑，它像一道横贯海湾的长虹，巍峨俊秀，气势磅礴，与举世闻名的悉尼歌剧院隔海相望，成为悉尼的象征。</h4>\r\n    <p style=\"padding: 0px; margin: 0px; line-height: 1.5;\">&nbsp;</p>\r\n    </li>\r\n    <li style=\"padding: 0px; margin: 0px 7px 0px 6px; float: left; width: 490px; height: 442px;\">\r\n    <div class=\"img\" style=\"padding: 0px; margin: 0px 0px 10px; overflow: hidden; height: 302px;\"><img alt=\"\" src=\"http://images4.c-ctrip.com/target/600a080000003j2633F09.jpg\" style=\"border: 0px; width: 490px;\" /></div>\r\n    <p class=\"img_txt\" style=\"padding: 0px; margin: 0px; line-height: 1.5; font-size: 15px;\">&nbsp;</p>\r\n    <h4 style=\"padding: 0px; margin: 0px;\">盛产于澳大利亚塔斯马尼亚岛的皇帝蟹素有&ldquo;蟹中之王&rdquo;的称号。它不仅肉质肥厚，又及其鲜美，让你咬下去的每一口都是那么充实。是姜葱爆炒？还是来个浓郁煲汤？你想要享用体型比面盆还大的螃蟹吗？没错！它就在美丽的悉尼等你。</h4>\r\n    <p style=\"padding: 0px; margin: 0px; line-height: 1.5;\">&nbsp;</p>\r\n    </li>\r\n    <li style=\"padding: 0px; margin: 0px 7px 0px 6px; float: left; width: 490px; height: 442px;\">\r\n    <div class=\"img\" style=\"padding: 0px; margin: 0px 0px 10px; overflow: hidden; height: 302px;\"><img alt=\"\" src=\"http://images4.c-ctrip.com/target/600l080000003j2651002.jpg\" style=\"border: 0px; width: 490px;\" /></div>\r\n    <p class=\"img_txt\" style=\"padding: 0px; margin: 0px; line-height: 1.5; font-size: 15px;\">&nbsp;</p>\r\n    <h4 style=\"padding: 0px; margin: 0px;\">龙虾在澳大利亚早已耳熟能详，但是作为龙虾界的佼佼者，塔斯马尼亚龙虾不能不提，不得不尝。这种龙虾就连本地人提起都啧啧称赞，清爽鲜美的味道，爽滑富有弹性的口感让人流连忘返。</h4>\r\n    <p style=\"padding: 0px; margin: 0px; line-height: 1.5;\">&nbsp;</p>\r\n    </li>\r\n    <li style=\"padding: 0px; margin: 0px 7px 0px 6px; float: left; width: 490px; height: 442px;\">\r\n    <div class=\"img\" style=\"padding: 0px; margin: 0px 0px 10px; overflow: hidden; height: 302px;\"><img alt=\"\" src=\"http://images4.c-ctrip.com/target/6009080000003j2996D50.jpg\" style=\"border: 0px; width: 490px;\" /></div>\r\n    <p class=\"img_txt\" style=\"padding: 0px; margin: 0px; line-height: 1.5; font-size: 15px;\">&nbsp;</p>\r\n    <h4 style=\"padding: 0px; margin: 0px;\">又叫牡蛎，澳大利亚的生蚝又肥又干净，也不贵。蚝可以生吃，也可以蒸吃。新鲜的蚝连盖都没有打开，顾客可以看着营业员开盖，当你品尝时如果担心有腥味的话，挤出几滴柠檬汁在蚝上，海水变得酸酸的清新起来，在鲜味的伴奏下，将蚝吞下，那种冰凉鲜甜的感觉，叫人即时恋上。</h4>\r\n    </li>\r\n</ul>', 'images/201701/thumb_img/179_thumb_G_1484619897651.jpg', 'images/201701/goods_img/179_G_1484619897888.jpg', 'images/201701/source_img/179_G_1484619897899.jpg', '1', '', '1', '1', '0', '56', '1484619897', '100', '0', '0', '0', '0', '0', '0', '1484619897', '0', '', '-1', '-1', '0', '');
